// Función helper para obtener productos por categoría
export const getProductsByCategory = (categoryId) => {
  return mockProducts.filter(product => product.category === categoryId);
};

// Función helper para obtener producto por ID
export const getProductById = (productId) => {
  return mockProducts.find(product => product.id === parseInt(productId));
};

// Función helper para obtener categoría por ID
export const getCategoryById = (categoryId) => {
  return categories.find(category => category.id === categoryId);
};

// Estadísticas útiles
export const getProductStats = () => {
  return {
    totalProducts: mockProducts.length,
    electronics: mockProducts.filter(p => p.category === 'electronics').length,
    clothing: mockProducts.filter(p => p.category === 'clothing').length,
    home: mockProducts.filter(p => p.category === 'home').length,
    totalStock: mockProducts.reduce((total, product) => total + product.stock, 0),
    averagePrice: Math.round(mockProducts.reduce((total, product) => total + product.price, 0) / mockProducts.length)
  };
};